import React, { useEffect, useState } from 'react';
import './Dashboard.css';

const Dashboard = () => {
  const [requests, setRequests] = useState([]);
  const [filteredRequests, setFilteredRequests] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  const [filterLectureName, setFilterLectureName] = useState('');
  const [filterResourcePerson, setFilterResourcePerson] = useState('');
  const [filterFacultyCoordinator, setFilterFacultyCoordinator] = useState('');
  const [filterDate, setFilterDate] = useState('');

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        // Replace with actual API endpoint once available
        const response = await new Promise((resolve) =>
          setTimeout(() => resolve({
            data: [
              { id: 1, lectureName: 'Advanced React', resourcePerson: 'John Doe', facultyCoordinator: 'Jane Smith', venue: 'Auditorium', date: '2024-07-01', attendees: 50 },
              { id: 2, lectureName: 'Machine Learning Basics', resourcePerson: 'Alice Johnson', facultyCoordinator: 'Robert Brown', venue: 'Room 101', date: '2024-07-05', attendees: 70 },
              { id: 3, lectureName: 'Introduction to AI', resourcePerson: 'Eve Adams', facultyCoordinator: 'Sophia Lee', venue: 'Room 202', date: '2024-07-10', attendees: 60 },
              { id: 4, lectureName: 'Cloud Computing', resourcePerson: 'Michael Scott', facultyCoordinator: 'David Beckham', venue: 'Conference Hall', date: '2024-07-15', attendees: 80 },
              { id: 5, lectureName: 'Cybersecurity Essentials', resourcePerson: 'Jessica White', facultyCoordinator: 'Emma Watson', venue: 'Auditorium', date: '2024-07-20', attendees: 90 },
              { id: 6, lectureName: 'Blockchain Technology', resourcePerson: 'Oliver Twist', facultyCoordinator: 'Liam Neeson', venue: 'Room 303', date: '2024-07-25', attendees: 40 },
              { id: 7, lectureName: 'Quantum Computing', resourcePerson: 'Sophia Turner', facultyCoordinator: 'Lucas Black', venue: 'Main Hall', date: '2024-08-01', attendees: 100 },
              { id: 8, lectureName: 'Data Science Workshop', resourcePerson: 'William Green', facultyCoordinator: 'Jack Reacher', venue: 'Lab 1', date: '2024-08-05', attendees: 110 },
              { id: 9, lectureName: 'Web Development', resourcePerson: 'Megan Fox', facultyCoordinator: 'Sarah Connor', venue: 'Lab 2', date: '2024-08-10', attendees: 55 },
              { id: 10, lectureName: 'Mobile App Development', resourcePerson: 'Jason Bourne', facultyCoordinator: 'Matt Damon', venue: 'Room 404', date: '2024-08-15', attendees: 65 },
            ]
          }), 1000)
        );
        setRequests(response.data);
        setFilteredRequests(response.data);
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch requests');
        setLoading(false);
      }
    };

    fetchRequests();
  }, []);

  useEffect(() => {
    const applyFilters = () => {
      let filtered = requests;

      if (filterLectureName) {
        filtered = filtered.filter(request =>
          request.lectureName.toLowerCase().includes(filterLectureName.toLowerCase())
        );
      }

      if (filterResourcePerson) {
        filtered = filtered.filter(request =>
          request.resourcePerson.toLowerCase().includes(filterResourcePerson.toLowerCase())
        );
      }

      if (filterFacultyCoordinator) {
        filtered = filtered.filter(request =>
          request.facultyCoordinator.toLowerCase().includes(filterFacultyCoordinator.toLowerCase())
        );
      }

      if (filterDate) {
        filtered = filtered.filter(request =>
          request.date === filterDate
        );
      }

      setFilteredRequests(filtered);
    };

    applyFilters();
  }, [filterLectureName, filterResourcePerson, filterFacultyCoordinator, filterDate, requests]);

  const getUniqueOptions = (key) => {
    return [...new Set(requests.map(request => request[key]))];
  };

  return (
    <div className="dashboard">
      <h2>Dashboard</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          {error && <div className="error">{error}</div>}
          <div className="filters">
            <select value={filterLectureName} onChange={(e) => setFilterLectureName(e.target.value)}>
              <option value="">All Lectures</option>
              {getUniqueOptions('lectureName').map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
            <select value={filterResourcePerson} onChange={(e) => setFilterResourcePerson(e.target.value)}>
              <option value="">All Resource Persons</option>
              {getUniqueOptions('resourcePerson').map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
            <select value={filterFacultyCoordinator} onChange={(e) => setFilterFacultyCoordinator(e.target.value)}>
              <option value="">All Faculty Coordinators</option>
              {getUniqueOptions('facultyCoordinator').map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
            <select value={filterDate} onChange={(e) => setFilterDate(e.target.value)}>
              <option value="">All Dates</option>
              {getUniqueOptions('date').map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>
          <table className="dashboard-table">
            <thead>
              <tr>
                <th>S.No</th>
                <th>Name of Lecture</th>
                <th>Resource Person</th>
                <th>Faculty Coordinator</th>
                <th>Venue</th>
                <th>Date</th>
                <th>Attendees</th>
              </tr>
            </thead>
            <tbody>
              {filteredRequests.map((request, index) => (
                <tr key={request.id}>
                  <td>{index + 1}</td>
                  <td>{request.lectureName}</td>
                  <td>{request.resourcePerson}</td>
                  <td>{request.facultyCoordinator}</td>
                  <td>{request.venue}</td>
                  <td>{request.date}</td>
                  <td>{request.attendees}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default Dashboard;
