import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import './HodProfile.css';

const HodProfile = () => {
  const [requests, setRequests] = useState([]);
  const [showRejectionModal, setShowRejectionModal] = useState(false);
  const [selectedRequestId, setSelectedRequestId] = useState(null);
  const [rejectionReason, setRejectionReason] = useState('');
  const hod = useSelector((state) => state.hod.hod);

  useEffect(() => {
    const fetchPendingRequests = async () => {
      try {
        const response = await axios.get('/lecture/pending-requests', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setRequests(response.data.requests);
      } catch (error) {
        console.error('Error fetching pending requests:', error);
      }
    };
    fetchPendingRequests();
  }, []);

  const handleApprove = async (requestId) => {
    try {
      await axios.post('/lecture/approve-request', { requestId }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setRequests(requests.filter((req) => req._id !== requestId));
    } catch (error) {
      console.error('Error approving request:', error);
    }
  };

  const handleReject = (requestId) => {
    setSelectedRequestId(requestId);
    setShowRejectionModal(true);
  };

  const handleRejectSubmit = async () => {
    try {
      await axios.post('/lecture/reject-request', { requestId: selectedRequestId, rejectionReason }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setRequests(requests.filter((req) => req._id !== selectedRequestId));
      setShowRejectionModal(false);
      setRejectionReason('');
    } catch (error) {
      console.error('Error rejecting request:', error);
    }
  };

  const generateReport = () => {
    const doc = new jsPDF();
    doc.autoTable({
      head: [['Date', 'Time', 'Topic', 'Attendees', 'Venue', 'Resource Person', 'Description']],
      body: requests.map((req) => [
        req.date, req.time, req.topic, req.attendees, req.venue, req.resourcePerson, req.description
      ]),
    });
    doc.save('pending_requests_report.pdf');
  };

  return (
    <div className="hod-profile">
      <header>
        <h2>HOD Profile</h2>
      </header>
      <main>
        {hod && (
          <div className="profile-info">
            <h3>{hod.name}</h3>
            <p><strong>Username:</strong> {hod.username}</p>
            <p><strong>Email:</strong> {hod.email}</p>
            <p><strong>Department:</strong> {hod.department}</p>
          </div>
        )}
        <div className="requests">
          <h3>Pending Requests</h3>
          {requests.length > 0 ? (
            <div>
              <button className="generate-report-btn" onClick={generateReport}>Generate Report</button>
              <table>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Topic</th>
                    <th>Attendees</th>
                    <th>Venue</th>
                    <th>Resource Person</th>
                    <th>Description</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {requests.map((req) => (
                    <tr key={req._id}>
                      <td>{req.date}</td>
                      <td>{req.time}</td>
                      <td>{req.topic}</td>
                      <td>{req.attendees}</td>
                      <td>{req.venue}</td>
                      <td>{req.resourcePerson}</td>
                      <td>{req.description}</td>
                      <td>
                        <button className="btn approve-btn" onClick={() => handleApprove(req._id)}>Approve</button>
                        <button className="btn reject-btn" onClick={() => handleReject(req._id)}>Reject</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p>No pending requests</p>
          )}
        </div>
      </main>
      {showRejectionModal && (
        <div className="rejection-modal">
          <div className="rejection-modal-content">
            <h4>Rejection Reason</h4>
            <textarea
              value={rejectionReason}
              onChange={(e) => setRejectionReason(e.target.value)}
              placeholder="Enter rejection reason"
            ></textarea>
            <button className="btn submit-btn" onClick={handleRejectSubmit}>Submit</button>
            <button className="btn cancel-btn" onClick={() => setShowRejectionModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default HodProfile;
